:LaengeAuftragsnummerLieferantKopf {
  a sh:NodeShape;
  sh:targetClass edifact-o:InvoiceDetails;

  {
    sh:property [
      sh:path edifact-o:hasDocumentType,
      sh:datatype xsd:string,
      sh:message "The order number of the supplier, RFF+VN, in the header is too long"
    ];

    sh:property [
      sh:path edifact-o:orderNumberSupplier,
      sh:datatype xsd:string,
      sh:minCount 1,
      sh:maxCount 1,
      sh:message "Order number must be a string with max length of 35 characters",
      sh:rule [
        sh:condition [
          sh:and [
            sh:object [
              sh:isOneOf (["RFF+VN"])
            ],
            sh:path (edifact-o:orderNumberSupplier)
          ]
        ],
        sh:consequence {
          sh:failure [
            sh:path edifact-o:orderNumberSupplier,
            sh:value { sh:length <= 35 }
          ]
        }
      ]
    ]
  }
}
