  targetClass org:FormalOrganization ;

  # Property constraint for 'hasDeliveryPartyRole'
  sh:property [
    sh:path org:hasDeliveryPartyRole ;
    sh:in [ sh:disjointWith "http://example.com/DeliveryPartyRole" ] ;
    sh:message "The data element 3164 is missing for the Segment NAD+DP, i.e. specification of the city of the delivery address." ;

    # Property constraint for 'hasCity'
    sh:property [
      sh:path edifact-o:hasCity ;
      sh:datatype xsd:string ;
      sh:minCount 1 ;
      sh:maxCount 1 ;
      sh:message "Invalid city value. Maximum length should be 35 characters." ;
      sh:message "If multiple values, please use a list." ;
      sh:message "Example: (Berlin, Munich)" ;

      # Check for max length
      sh:condition {
        sh:and [
          sh:shape {
            sh:path edifact-o:hasCity ;
            sh:datatype xsd:string ;
            sh:maxLength 35
          },
          sh:not (
            sh:exists { ?o :hasCity ?city }
              && sh:text { ?city } < "35"
          )
        ]
      }
    ]
}
