    sh:and ( [ sh:property [ sh:datatype xsd:string ;
                        sh:path edu:activityName ] ] [ sh:property [ sh:datatype xsd:integer ;
                        sh:minInclusive 1 ;
                        sh:path edu:weeklyHours ] ] ) ;
    sh:message "Extracurricular record must include activity name and hours per week." ;
