@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:DiskQuotaShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:datatype xsd:integer ;
                        sh:minInclusive 1 ;
                        sh:path it:allocatedGB ] ] [ sh:property [ sh:datatype xsd:string ;
                        sh:minLength 2 ;
                        sh:path it:mountPoint ] ] ) ;
    sh:message "Disk quota must define allocated space and a valid mount point." ;
    sh:targetClass it:DiskQuota .

@prefix : <http://mapping.example.com/> .
@prefix it: <http://example.org/it#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

