@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:EnrollmentStatusShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:datatype xsd:boolean ;
                        sh:path edu:isActive ] ] [ sh:property [ sh:datatype xsd:string ;
                        sh:minLength 3 ;
                        sh:path edu:semesterName ] ] ) ;
    sh:message "Enrollment status must indicate if active and specify the semester." ;
    sh:targetClass edu:EnrollmentStatus .

@prefix : <http://mapping.example.com/> .
@prefix edu: <http://example.org/edu#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:DigitalBadgeShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:datatype xsd:string ;
                        sh:minLength 4 ;
                        sh:path edu:badgeTitle ] ] [ sh:property [ sh:datatype xsd:date ;
                        sh:path edu:issuedOn ] ] ) ;
    sh:message "Each digital badge must have a title and issue date." ;
    sh:targetClass edu:DigitalBadge .

@prefix : <http://mapping.example.com/> .
@prefix edu: <http://example.org/edu#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:AlumniRecordShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:datatype xsd:gYear ;
                        sh:path edu:graduationYear ] ] [ sh:property [ sh:datatype xsd:string ;
                        sh:minLength 3 ;
                        sh:path edu:degreeAwarded ] ] ) ;
    sh:message "Alumni record must contain graduation year and awarded degree." ;
    sh:targetClass edu:AlumniRecord .

@prefix : <http://mapping.example.com/> .
@prefix edu: <http://example.org/edu#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:LearningOutcomeShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:datatype xsd:string ;
                        sh:minLength 10 ;
                        sh:path edu:outcomeText ] ] [ sh:property [ sh:minCount 1 ;
                        sh:node edu:Course ;
                        sh:path edu:linkedCourse ] ] ) ;
    sh:message "Learning outcome must describe the result and be linked to a course." ;
    sh:targetClass edu:LearningOutcome .

@prefix : <http://mapping.example.com/> .
