@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:MentorshipProgramShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:path edu:mentor ;
                        sh:qualifiedMinCount 1 ;
                        sh:qualifiedValueShape [ sh:class edu:FacultyMember ] ] ] [ sh:property [ sh:datatype xsd:integer ;
                        sh:minInclusive 1 ;
                        sh:path edu:menteeCount ] ] ) ;
    sh:message "Mentorship program must have at least one faculty mentor and a mentee count." ;
    sh:targetClass edu:MentorshipProgram .

@prefix : <http://mapping.example.com/> .
@prefix edu: <http://example.org/edu#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:LanguageTestShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:datatype xsd:string ;
                        sh:path edu:testName ] ] [ sh:property [ sh:datatype xsd:float ;
                        sh:maxInclusive 100.0 ;
