@prefix : <http://mapping.example.com/> .
@prefix edu: <http://example.org/edu#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:TeachingAssignmentShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:minCount 1 ;
                        sh:node edu:FacultyMember ;
                        sh:path edu:assignedFaculty ] ] [ sh:property [ sh:datatype xsd:integer ;
                        sh:minInclusive 1 ;
                        sh:path edu:teachingHours ] ] ) ;
