    sh:targetClass edu:TeachingAssignment .

@prefix : <http://mapping.example.com/> .
@prefix edu: <http://example.org/edu#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:HonorRollShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:datatype xsd:string ;
                        sh:minLength 3 ;
                        sh:path edu:studentName ] ] [ sh:property [ sh:datatype xsd:float ;
                        sh:maxInclusive 4.0 ;
                        sh:minInclusive 3.5 ;
                        sh:path edu:GPA ] ] ) ;
    sh:message "Honor roll entry must include student name and GPA ≥ 3.5." ;
