:ClientMustHaveEmergencyOrOrg a sh:NodeShape ;
    sh:message "Client must have either an emergency contact or be part of an organization." ;
    sh:or ( [ sh:property [ sh:class foaf:Person ;
                        sh:minCount 1 ;
                        sh:path :emergencyContact ] ] [ sh:property [ sh:class org:Organization ;
                        sh:minCount 1 ;
                        sh:path foaf:member ] ] ) ;
    sh:targetClass foaf:Person .

