    sh:and ( [ sh:property [ sh:class foaf:OnlineAccount ;
                        sh:path foaf:account ] ] [ sh:property [ sh:in ( "Premium" "Enterprise" ) ;
                        sh:path :accountTier ] ] ) ;
    sh:message "Only Premium or Enterprise clients can have linked online accounts." ;
    sh:targetClass foaf:Person .

