@prefix vcard: <http://www.w3.org/2006/vcard/ns#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:ClientRegionOrCountryShape a sh:NodeShape ;
    sh:message "Client must provide either a region or a country." ;
    sh:or ( [ sh:property [ sh:datatype xsd:string ;
                        sh:path vcard:region ] ] [ sh:property [ sh:datatype xsd:string ;
                        sh:path vcard:country-name ] ] ) ;
    sh:targetClass foaf:Person .

@prefix : <http://mapping.example.com/> .
