@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:ClientIdentifierShape a sh:NodeShape ;
    sh:message "Each client must have a unique identifier assigned." ;
    sh:property [ sh:class cmns-id:Identifier ;
            sh:minCount 1 ;
            sh:path cmns-id:identifies ] ;
    sh:targetClass foaf:Person .

@prefix : <http://mapping.example.com/> .
@prefix foaf: <http://xmlns.com/foaf/0.1/> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:ClientValidLocationFormat a sh:NodeShape ;
    sh:message "Location coordinates must follow the format 'latitude,longitude'." ;
    sh:property [ sh:datatype xsd:string ;
            sh:path :locationCoordinates ;
            sh:pattern "^-?\\d+(\\.\\d+)?,-?\\d+(\\.\\d+)?$" ] ;
    sh:targetClass foaf:Person .

@prefix : <http://mapping.example.com/> .
@prefix foaf: <http://xmlns.com/foaf/0.1/> .
@prefix sh: <http://www.w3.org/ns/shacl#> .

:ClientMustNotBeAnonymous a sh:NodeShape ;
    sh:message "Client name cannot be 'Anonymous'." ;
    sh:not [ sh:property [ sh:hasValue "Anonymous" ;
                    sh:path foaf:name ] ] ;
    sh:targetClass foaf:Person .

@prefix : <http://mapping.example.com/> .
@prefix foaf: <http://xmlns.com/foaf/0.1/> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .

:ClientAccountStatusLogic a sh:NodeShape ;
    sh:message "Premium clients must have Active account status." ;
    sh:or ( [ sh:property [ sh:hasValue "Active" ;
                        sh:path :accountStatus ] ] [ sh:not [ sh:property [ sh:hasValue "Premium" ;
                                sh:path :accountTier ] ] ] ) ;
    sh:targetClass foaf:Person .

@prefix : <http://mapping.example.com/> .
