:GroupTicketLimitShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:class schema1:Event ;
                        sh:path schema1:isPartOf ] ] [ sh:property [ sh:datatype xsd:integer ;
                        sh:maxInclusive 100 ;
                        sh:path schema1:totalTicketLimit ] ] ) ;
    sh:message "Group tickets must belong to an event and have a total limit of 100 or fewer." ;
    sh:targetClass schema1:Ticket .

@prefix : <http://mapping.example.com/> .
