:VIPTicketQualificationShape a sh:NodeShape ;
    sh:message "VIP tickets must be priced at least €100." ;
    sh:property [ sh:hasValue "VIP" ;
            sh:path schema1:category ;
            sh:qualifiedMinCount 1 ;
            sh:qualifiedValueShape [ sh:property [ sh:minInclusive 100.00 ;
                            sh:path schema1:price ] ] ] ;
    sh:targetClass schema1:Ticket .

@prefix : <http://mapping.example.com/> .
