:UserCredentialLogicShape a sh:NodeShape ;
    sh:message "User must have either a secure password or biometric authentication." ;
    sh:or ( [ sh:property [ sh:datatype xsd:string ;
                        sh:minLength 8 ;
                        sh:path it:hasPassword ] ] [ sh:property [ sh:datatype xsd:boolean ;
                        sh:hasValue true ;
                        sh:path it:hasBiometricAuth ] ] ) ;
    sh:targetClass it:User .

@prefix : <http://mapping.example.com/> .
@prefix it: <http://example.org/it#> .
