:TwoFactorAndSecureRoleShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:hasValue true ;
                        sh:path it:requiresTwoFactor ] ] [ sh:property [ sh:minInclusive 4 ;
                        sh:path it:accessLevel ] ] ) ;
    sh:message "High-access policies must require two-factor authentication." ;
    sh:targetClass it:AccessPolicy .

@prefix : <http://mapping.example.com/> .
@prefix it: <http://example.org/it#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:ResourceAccessXORShape a sh:NodeShape ;
    sh:message "Policy can grant either read or write access, but not both." ;
    sh:not [ sh:and ( [ sh:property [ sh:hasValue true ;
                                sh:path it:hasReadAccess ] ] [ sh:property [ sh:hasValue true ;
                                sh:path it:hasWriteAccess ] ] ) ] ;
    sh:or ( [ sh:property [ sh:hasValue true ;
                        sh:path it:hasReadAccess ] ] [ sh:property [ sh:hasValue true ;
                        sh:path it:hasWriteAccess ] ] ) ;
    sh:targetClass it:AccessPolicy .

