:IPRangeAndAuthMethodShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:path it:allowedIPRange ;
                        sh:pattern "^\\d{1,3}(\\.\\d{1,3}){3}/\\d{1,2}$" ] ] [ sh:property [ sh:in ( "Token" "Biometric" ) ;
                        sh:path it:authenticationMethod ] ] ) ;
    sh:message "Token or Biometric-based policies must specify a valid IP range." ;
    sh:targetClass it:AccessPolicy .

