                        sh:path catalog:availability ] ] [ sh:property [ sh:datatype xsd:boolean ;
                        sh:hasValue true ;
                        sh:path catalog:discontinued ] ] ) ;
    sh:targetClass catalog:ProductStatus .

@prefix : <http://mapping.example.com/> .
@prefix catalog: <http://example.com/catalog#> .
