@prefix : <http://mapping.example.com/> .
@prefix catalog: <http://example.com/catalog#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:ProductLabelShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:datatype xsd:string ;
                        sh:minLength 3 ;
                        sh:path catalog:labelName ] ] [ sh:property [ sh:datatype xsd:boolean ;
                        sh:path catalog:isEcoFriendly ] ] ) ;
    sh:message "Product label must include name and eco-friendly status." ;
    sh:targetClass catalog:ProductLabel .

@prefix : <http://mapping.example.com/> .
@prefix catalog: <http://example.com/catalog#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:CustomerReviewShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:datatype xsd:float ;
                        sh:maxInclusive 5.0 ;
                        sh:minInclusive 1.0 ;
                        sh:path catalog:rating ] ] [ sh:property [ sh:datatype xsd:string ;
                        sh:minLength 10 ;
                        sh:path catalog:reviewText ] ] ) ;
    sh:message "Customer review must include a rating between 1 and 5 and a review message." ;
    sh:targetClass catalog:CustomerReview .

:SeasonalAvailabilityShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:datatype xsd:gMonth ;
                        sh:path catalog:startMonth ] ] [ sh:property [ sh:datatype xsd:gMonth ;
                        sh:path catalog:endMonth ] ] ) ;
    sh:message "Seasonal availability must define start and end months." ;
    sh:targetClass catalog:SeasonalAvailability .

@prefix : <http://mapping.example.com/> .
@prefix catalog: <http://example.com/catalog#> .
