@prefix : <http://mapping.example.com/> .
@prefix catalog: <http://example.com/catalog#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:PriceRangeShape a sh:NodeShape ;
    sh:and ( [ sh:property [ sh:datatype xsd:float ;
                        sh:minInclusive 0.0 ;
                        sh:path catalog:minPrice ] ] [ sh:property [ sh:datatype xsd:float ;
                        sh:path catalog:maxPrice ] ] [ sh:sparql [ a sh:SPARQLConstraint ;
                        sh:message "Maximum price must be greater than or equal to minimum price." ;
                        sh:prefixes [ sh:declare [ sh:namespace "http://example.org/catalog#"^^xsd:anyURI ;
                                        sh:prefix "catalog" ] ] ;
                        sh:select """
                SELECT $this
                WHERE {
                    $this catalog:minPrice ?min .
                    $this catalog:maxPrice ?max .
                    FILTER (?max < ?min)
                }
            """ ] ] ) ;
    sh:message "Price range must include valid minimum and maximum prices." ;
    sh:targetClass catalog:PriceRange .

